<section id="container" class="login">
    <article id="login">
        <h1>login</h1>
        <form id="form">
            <div class="alert-box">Name or/and password not correct. Please check again.</div>
            <fieldset class="name">
                <label for="name">name</label>
                <input type="text" id="name" placeholder="Welcome! How may we call you?">
            </fieldset>
            <fieldset class="email">
                <label for="email">email</label>
                <input type="email" id="email" placeholder="What is your e-mail address?">
            </fieldset>
            <fieldset class="password">
                <label for="password">password</label>
                <input type="password" id="password" placeholder="Insert a secret password">
            </fieldset>
            <fieldset>
                <button class="register">register</button>
                <input type="submit" id="submit" value="login">
            </fieldset>
        </form>
    </article>

    <div class="artist-credits">
        <span><a href="https://unsplash.com/aleskrivec" class="artist">Alex Krivec</a> | <a href="unsplash.com">unsplash.com</a></span>
    </div>
</section>